sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (newValue === '') {
      return;
   }
	//alert("NEW VALUE: " + newValue);

	var whatneeded = g_form.getValue('u_what_needed');
	g_form.clearOptions('u_what_needed');
	if(newValue=='hr'){
		g_form.addOption('u_what_needed', 'hr11', 'San Test HumanResource1');
		g_form.addOption('u_what_needed', 'hr21', 'San Test HumanResource2');
		g_form.addOption('u_what_needed', 'other1', 'San Test Other');
	}

	if(newValue=='facilities'){
		g_form.addOption('u_what_needed', 'facilities11', 'San Test Facilities1');
		g_form.addOption('u_what_needed', 'facilities21', 'San Test Facilities2');
		g_form.addOption('u_what_needed', 'other1', 'San Test Other');
	}

	if(newValue == 'legal'){
		g_form.addOption('u_what_needed', 'legal11', 'San Test Legal1');
		g_form.addOption('u_what_needed', 'legal21', 'San Test Legal2');
		g_form.addOption('u_what_needed', 'other1', 'San Test Other');
	}

	if(isLoading && !g_form.isNewRecord()){
		g_form.setValue('u_what_needed', whatneeded);
	}


}
